import React, { useState } from "react";

const Chatbot = () => {
  const [isOpen, setIsOpen] = useState(false);
  const [messages, setMessages] = useState([]);
  const [userInput, setUserInput] = useState("");

  const toggleChatbot = () => {
    setIsOpen(!isOpen);
  };

  const sendMessage = async () => {
    if (!userInput.trim()) return;

    const userMessage = { sender: "user", text: userInput };
    setMessages([...messages, userMessage, { sender: "bot", text: "Typing..." }]);
    setUserInput("");

    try {
      const response = await fetch("http://127.0.0.1:5000/chatbot", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ message: userInput }),
      });

      const data = await response.json();
      setMessages((prevMessages) => [...prevMessages.slice(0, -1), { sender: "bot", text: data.response }]);
    } catch (error) {
      console.error("Chatbot API error:", error);
    }
  };

  return (
    <div className="fixed bottom-4 right-4 flex flex-col items-end">
      <button onClick={toggleChatbot} className="p-4 bg-blue-500 text-white rounded-full shadow-lg">
        ðŸ’¬
      </button>
      {isOpen && (
        <div className="w-80 bg-white rounded-lg shadow-lg overflow-hidden mt-2">
          <div className="flex justify-between items-center bg-blue-500 text-white p-2">
            <span>eAsha Health Assistant</span>
            <button onClick={toggleChatbot} className="text-lg">âœ–</button>
          </div>
          <div className="h-64 overflow-y-auto p-2 border-b">
            {messages.map((msg, index) => (
              <div key={index} className={`p-2 my-1 ${msg.sender === "user" ? "text-right" : "text-left"}`}>
                <strong>{msg.sender === "user" ? "You" : "Bot"}:</strong> {msg.text}
              </div>
            ))}
          </div>
          <div className="flex p-2">
            <input
              type="text"
              className="flex-grow p-2 border rounded"
              placeholder="Ask me about health..."
              value={userInput}
              onChange={(e) => setUserInput(e.target.value)}
              onKeyPress={(e) => e.key === "Enter" && sendMessage()}
            />
            <button onClick={sendMessage} className="ml-2 p-2 bg-blue-500 text-white rounded">
              Send
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default Chatbot;
