import React, { useState } from "react";
import "./Chatbot.css";

const Chatbot = () => {
    const [messages, setMessages] = useState([]);
    const [input, setInput] = useState("");
    const [isTyping, setIsTyping] = useState(false);
    const [isOpen, setIsOpen] = useState(false);
    
    const sendMessage = async () => {
        if (!input.trim()) return;
        
        const newMessages = [...messages, { sender: "user", text: input }];
        setMessages(newMessages);
        setInput("");
        setIsTyping(true);
        
        try {
            const response = await fetch("/chatbot", {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify({ message: input })
            });
            const data = await response.json();
            
            setMessages([...newMessages, { sender: "bot", text: data.response }]);
        } catch (error) {
            setMessages([...newMessages, { sender: "bot", text: "Sorry, something went wrong." }]);
        }
        setIsTyping(false);
    };

    const handleKeyPress = (event) => {
        if (event.key === "Enter") {
            sendMessage();
        }
    };

    const sendQuickOption = (option) => {
        setInput(option);
        sendMessage();
    };
    
    return (
        <>
            <div className="chatbot-icon" onClick={() => setIsOpen(!isOpen)}>
                ðŸ’¬
            </div>
            {isOpen && (
                <div className="chatbot-container">
                    <div className="chat-header">eAsha Health Assistant <button onClick={() => setIsOpen(false)}>âœ–</button></div>
                    <div className="chat-body">
                        {messages.map((msg, index) => (
                            <div key={index} className={`chat-message ${msg.sender}-message`}>
                                <strong>{msg.sender === "user" ? "You" : "Bot"}:</strong> {msg.text}
                            </div>
                        ))}
                        {isTyping && <div className="chat-message bot-message">Bot: Typing...</div>}
                    </div>
                    <div className="quick-options">
                        <button onClick={() => sendQuickOption("Doctor")}>ðŸ©º</button>
                        <button onClick={() => sendQuickOption("Medicine")}>ðŸ’Š</button>
                        <button onClick={() => sendQuickOption("Appointment")}>ðŸ“…</button>
                    </div>
                    <div className="chat-input">
                        <input type="text" value={input} onChange={(e) => setInput(e.target.value)} onKeyPress={handleKeyPress} placeholder="Ask me about health..." />
                        <button onClick={sendMessage}>âž¤</button>
                    </div>
                </div>
            )}
        </>
    );
};

export default Chatbot;
