import React, { useState } from "react";
import "./Chatbot.css";

const Chatbot = () => {
    const [messages, setMessages] = useState([
        { sender: "bot", text: "Hello! I am eAsha Health Care. How can I assist you today?" }
    ]);
    const [input, setInput] = useState("");
    const [isTyping, setIsTyping] = useState(false);
    const [isOpen, setIsOpen] = useState(false);
    const [quickOptions, setQuickOptions] = useState(["Doctor", "Medicine", "Appointment"]);

    const keywordSuggestions = {
        fever: ["Consult Doctor", "Fever Medicine"],
        headache: ["Pain Relief", "Consult Doctor"],
        cough: ["Cough Syrup", "Consult Doctor"],
        diabetes: ["Sugar Test", "Diabetes Medicine"],
    };

    const sendMessage = async () => {
        if (!input.trim()) return;
        let userMessage = input.trim().toLowerCase();

        // Extract relevant keywords from user message
        let suggestedOptions = [];
        Object.keys(keywordSuggestions).forEach((keyword) => {
            if (userMessage.includes(keyword)) {
                suggestedOptions = keywordSuggestions[keyword];
            }
        });

        // Update quick options based on detected keywords
        if (suggestedOptions.length > 0) {
            setQuickOptions(suggestedOptions);
        } else {
            setQuickOptions(["Doctor", "Medicine", "Appointment"]);
        }

        const newMessages = [...messages, { sender: "user", text: userMessage }];
        setMessages(newMessages);
        setInput("");
        setIsTyping(true);

        try {
            const response = await fetch("http://127.0.0.1:5000/chatbot", {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify({ message: userMessage })
            });
            
            if (!response.ok) throw new Error(`HTTP error! Status: ${response.status}`);
            
            const data = await response.json();
            setMessages([...newMessages, { sender: "bot", text: data.response }]);
        } catch (error) {
            console.error("Error fetching chatbot response:", error);
            setMessages([...newMessages, { sender: "bot", text: "Error: Unable to reach chatbot." }]);
        }
        setIsTyping(false);
    };

    const handleKeyPress = (event) => {
        if (event.key === "Enter") {
            sendMessage();
        }
    };

    const sendQuickOption = (option) => {
        setInput(option);
        sendMessage();
    };

    return (
        <>
            <div className="chatbot-icon" onClick={() => setIsOpen(!isOpen)}>ðŸ’¬</div>

            {isOpen && (
                <div className="chatbot-container">
                    <div className="chat-header">eAsha Health Assistant <button onClick={() => setIsOpen(false)}>âœ–</button></div>
                    <div className="chat-body">
                        {messages.map((msg, index) => (
                            <div key={index} className={`chat-message ${msg.sender}-message`}>
                                <strong>{msg.sender === "user" ? "You" : "Bot"}:</strong> {msg.text}
                            </div>
                        ))}
                        {isTyping && <div className="chat-message bot-message">Bot: Typing...</div>}
                    </div>
                    <div className="quick-options">
                        {quickOptions.map((option, index) => (
                            <button key={index} onClick={() => sendQuickOption(option)}>
                                {option}
                            </button>
                        ))}
                    </div>
                    <div className="chat-input">
                        <input type="text" value={input} onChange={(e) => setInput(e.target.value)} onKeyPress={handleKeyPress} placeholder="Ask me about health..." />
                        <button onClick={sendMessage}>âž¤</button>
                    </div>
                </div>
            )}
        </>
    );
};

export default Chatbot;
