import React, { useState } from "react";

const Chatbot = () => {
  const [isOpen, setIsOpen] = useState(false);
  const [messages, setMessages] = useState([]);
  const [userInput, setUserInput] = useState("");

  const toggleChatbot = () => {
    setIsOpen(!isOpen);
  };

  const handleKeyPress = (event) => {
    if (event.key === "Enter") {
      sendMessage();
    }
  };

  const sendMessage = async () => {
    if (!userInput.trim()) return;
    
    const userMessage = { sender: "user", text: userInput };
    setMessages([...messages, userMessage, { sender: "bot", text: "Typing..." }]);
    setUserInput("");
    
    try {
      const response = await fetch("/chatbot", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ message: userInput })
      });
      const data = await response.json();
      
      setMessages((prevMessages) => {
        return [...prevMessages.slice(0, -1), { sender: "bot", text: data.response }];
      });
    } catch (error) {
      console.error("Error fetching chatbot response:", error);
    }
  };

  const sendOption = (option) => {
    setUserInput(option);
    sendMessage();
  };

  return (
    <div className="fixed bottom-4 right-4 flex flex-col items-end">
      <div
        className="cursor-pointer p-4 bg-blue-500 text-white rounded-full shadow-lg"
        onClick={toggleChatbot}
      >
        ðŸ’¬
      </div>
      {isOpen && (
        <div className="w-80 bg-white rounded-lg shadow-lg overflow-hidden mt-2">
          <div className="flex justify-between items-center bg-blue-500 text-white p-2">
            <span>eAsha Health Assistant</span>
            <button onClick={toggleChatbot} className="text-lg">âœ–</button>
          </div>
          <div className="h-64 overflow-y-auto p-2 border-b">
            {messages.map((msg, index) => (
              <div key={index} className={`p-2 my-1 ${msg.sender === "user" ? "text-right" : "text-left"}`}>
                <strong>{msg.sender === "user" ? "You" : "Bot"}:</strong> {msg.text}
              </div>
            ))}
          </div>
          <div className="flex justify-around p-2 border-b">
            <button onClick={() => sendOption("Doctor")} className="p-2 bg-gray-200 rounded">ðŸ©º</button>
            <button onClick={() => sendOption("Medicine")} className="p-2 bg-gray-200 rounded">ðŸ’Š</button>
            <button onClick={() => sendOption("Appointment")} className="p-2 bg-gray-200 rounded">ðŸ“…</button>
          </div>
          <div className="flex p-2">
            <input
              type="text"
              className="flex-grow p-2 border rounded"
              placeholder="Ask me about health..."
              value={userInput}
              onChange={(e) => setUserInput(e.target.value)}
              onKeyPress={handleKeyPress}
            />
            <button onClick={sendMessage} className="ml-2 p-2 bg-blue-500 text-white rounded">
              <img src="https://cdn-icons-png.flaticon.com/512/786/786205.png" alt="Send" width="20" />
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default Chatbot;
